import React from 'react';
class Header extends React.Component {
    async getWeather(){
    let output='getting weather';
    let degrees='';
    let weatherDescription='';
    let weatherIcon='';
    document.getElementById('weatherDegrees').innerHTML = 'getting weather';
    const url = 'http://api.weatherstack.com/current?access_key=6515e85ac5aa99126314670e1eef2f9c&query=Mombasa&units=f';
    const weatherRetrieved = await fetch(url, {
        method: 'GET'
    })
    .then(response => response.json())
    .then(data => {
        degrees = data.current.temperature;
        weatherDescription = data.current.weather_descriptions;
        weatherIcon = data.current.weather_icons;
        return {degrees, weatherDescription, weatherIcon};
    })
    document.getElementById('weatherDegrees').innerHTML = weatherRetrieved.degrees + ' degrees';
    document.getElementById('weatherDescription').innerHTML = weatherRetrieved.weatherDescription;
    document.getElementById('weatherImage').src = weatherRetrieved.weatherIcon;
    }
    render() {
        return(
            <div data-testid="headerTest" className="header">
                <div></div>
                <h1 id="pageTitle">Callouts</h1>
                <div id="weather">
                    <h3>Current weather in new Mombasa</h3>
                    <sp id="weatherDegrees"></sp>
                    <button onClick={getWeather()}></button>
                    <sp>weather icon</sp>
                </div>
                
            </div>
        );
    }
}
export default Header;
